import { useState } from "react";
import MovieCard from "./MovieCard";
import AddNewMovie from "./AddNewMovie";

const MovieList = () => {
    
    const [movies, setMovies] = useState([
        {id: Math.random(),title: "Titanic",description: "4 Oscars winner, romance",posterURL: "data:image/jpeg;base64,",rating: 5},
        {id: Math.random(), title: "Underworld",description: "Horror",posterURL: "data:image/jpeg;base64,",rating: 3},
        {id: Math.random(), title: "Matrix",description: "Science fiction",posterURL: "data:image/jpeg;base64,",rating: 5},
        {id: Math.random(), title : "Smile",  description: "movie", posterURL :"https://dx35vtwkllhj9.cloudfront.net/paramountpictures/smile/images/regions/us/onesheet.jpg", rating: 2}
    ]);
    const addMovie = (movie) => {
        setMovies([...movies, movie]);
    }

    const [sortBy, setSortBy] = useState('');
    

    const handleSortChange = (event) => {
        setSortBy(event.target.value);
    };

    const sortMovies = (a, b) => {
        const nameA = a.title.toLowerCase();
        const nameB = b.title.toLowerCase();
        if (sortBy === "A2Z") {
            return nameA.localeCompare(nameB);
        }
        else if (sortBy === "Z2A" ){
            return nameB.localeCompare(nameA);
        }
        else if (sortBy === 'rating') {
            return b.rating - a.rating;
        } else {
            return 0;
        }
    };
    
    
  const sortedMovies = [...movies].sort(sortMovies);
 


    
    
    

    return(
        <div>
            <div className="sort-dropdown">
                <label htmlFor="sort-criteria">Sort by:</label>
                <select id="sort-criteria" value={sortBy} onChange={handleSortChange}>
                    <option value="">-- Select criteria --</option>
                    <option value="A2Z">Title from A to Z</option>
                    <option value="Z2A">Title from Z to A</option>
                    <option value="rating">Rating</option>
                </select>
            </div>
            {console.log(sortedMovies)}
            <ul>
            
                {sortedMovies.map((movie) => {
                    
                    return (
                        
                        <ul>
                        <MovieCard 
                            key={movie.id}
                            name={movie.title} 
                            posterUrl = {movie.posterURL} 
                            description = {movie.description} 
                            rating = {movie.rating}>  
                        </MovieCard>      
                        </ul>                  
                    )
                    
                })}
            </ul>
            <div>
                <AddNewMovie onAddMovie = {addMovie}></AddNewMovie>
            </div>
            


        </div>  
    )
};

export default MovieList;


